export const CLIENT_NAME = 'client-name';
export const AUTH_TOKEN = 'auth-token';
export const PORTAL_NAME = 'portal-name';
export const USER_ROLE = 'user-role';
export const INVALID_CREDENTIALS = 'invalid-credentials';
export const HTTP_STATUS_OK = 200;
export const HTTP_BAD_REQUEST = 400;
export const GENERIC_SUCCESS = "Success";
export const GENERIC_ERROR = "API level error"
export const ACCOUNT_VALIDATION_ERROR = "Account Validation Error";
export const RECAPTCHA_ERROR = "Recaptcha Error";
export const SECURITY_VERIFICATION_TITLE = "Security Verification";
export const FORGOT_PWD_SUCCESS = "Mail Sent";
export const DEVICE_BREAKPOINTS = {
  xs: '480px',
  sm: '576px',
  md: '768px',
  lg: '992px',
  xl: '1200px',
  xxl: '1600px',
};

export const APIMETHODS = {
  GET: "GET",
  POST: "POST",
  PUT: "PUT",
  PATCH: "PATCH",
  DELETE: "DELETE"
}

export const UIPasswordMinLength = 8;
export const UIPasswordMaxLength = 16;

//Portal Names
export const CCB_PORTAL = "CCB";
export const P2C_PORTAL = "P2C";

//Payment IFrame constants

export const PaymentIFrameConstants = { WorldPay: "Worldpay", iCheckGateWay: "iCheckGateway", ChasePaymentTech: "PaymentechGateway" }
export const PaymentIFrameCheckCreditCard = "GatewayCimInterface"
export const PaymentIFrameCheckECheck = "GatewayECheckCimInterface"
export const PaymentIFrameHostedUrl = "GatewayHostedURL"
export const PaymentIFrameCheckValue = "CCVaultPCI"
export const PaymentGatewayAccountType = "echeck";
//export const PaymentType = {CreditCard : 'CreditCard', eCheck:"eCheck" }
export const PaymentIFrameGatewayHostedOptions = "GatewayHostedOpts"
export const PaymentIFrameGatewayHostedCodes = "GatewayHostedCodes"
export const PaymentIFrameUIDSettings = "uIDSettings"
export const PaymentACHQualification = "ACHQualification"
export const PaymentCCQualification = "CCQualification"

export const PaymentGatewaySettings = [
  {
    PaymentGatewayType: PaymentIFrameConstants.WorldPay,
    QueryParams: "sessionid",
    CreditCardPage: "showSession.asp",
    AddWindowEventListener: false,
    CallApiOnMount: true,
    Width: "500px",
    Height: "250px"
  },
  {
    PaymentGatewayType: PaymentIFrameConstants.iCheckGateWay,
    QueryParams: "", // from config
    CreditCardPage: "", // from config
    AddWindowEventListener: true,
    CallApiOnMount: false,
    Width: "500px",
    Height: "400px"
  },
  { //TODO
    PaymentGatewayType: PaymentIFrameConstants.ChasePaymentTech,
    QueryParams: "", // from config
    CreditCardPage: "", // from config
    AddWindowEventListener: false,
    CallApiOnMount: false
  }
];

export const PaymentType = {
  CreditCard: 'C',
  BankAccount: '6'
}

export const PaymentOptions = [
  { label: 'Bank Draft', value: PaymentType.BankAccount },
  { label: 'Credit Card', value: PaymentType.CreditCard }
];

// Payment Methods
export const PaymentMethod = {
  AutoPay: 'AUTOPAY',
  OneTime: 'ONETIME',
  OneTimeSave: 'ONETIMESAVE',
  SavePaymentMethod: 'SAVE'
}

export const PaymentFrequency = [
  { label: 'Set up AutoPay', value: PaymentMethod.AutoPay },
  { label: 'Save Payment Method', value: PaymentMethod.SavePaymentMethod }
];

export const Account_Type_CCard = "ccard";

// Payment Allocation constants
export const PAYAUTH = 'payAuths';
export const PAYAUTH_KEY = 'payAuth';
export const PAYAUTH_TYPE = 'payAllocation';
export const PAYAUTH_ENABLED = 'Y';

// Cookies
export const LOCALSTORAGE_LOGGEDIN_USER_KEY = 'loggedInUser';
export const LOCALSTORAGE_CUSTOMER_SAVED_SEARCH_KEY = 'customer';

//export const BASE_URL = 'https://apiesgglobal-beta.herokuapp.com/';
export const BASE_URL = 'http://localhost:8080/';

export const MAX_SAVED_SEARCH = 'Maximum 3 saved searches allowed per user';
export const SAVED_SEARCH_ALREADY_EXISTS = 'Saved search with same name is present, do you want update same?';

export const BLANK_VALUE = "__";
export const EMPTY_VALUE = "";
export const YES = "Y";
export const NO = "N";

export const CURRENCY = "USD";
export const USAGE_UNIT = "kWh";
export const DEFAULT_COUNTRY = "USA";
export const SELECTED_DATE_AFTER = "1899-12-31"

//Antd Card Default Padding
export const ANTD_CARD_DEFAULT_PADDING = 24;

export const WRITE_OFF_KEY = "WriteOff";

//Color Code for Customer Status
export const CUSTOMER_STATUS_COLOR_CODES = [
  {
    status: "A",
    statusDesc: "Active",
    color: "green"
  },
  {
    status: "C",
    statusDesc: "Cancel",
    color: "gold"
  },
  {
    status: "G",
    statusDesc: "Going Final",
    color: "red"
  },
  {
    status: "I",
    statusDesc: "Inactive",
    color: "gold"
  },
  {
    status: "I$",
    statusDesc: "Arrear",
    color: "gold"
  },
  {
    status: "IC",
    statusDesc: "Inactive Collections",
    color: "gold"
  },
  {
    status: "IRA",
    statusDesc: "Ready for Arrears",
    color: "geekblue"
  },
  {
    status: "P",
    statusDesc: "Pending for Switch",
    color: "geekblue"
  },
  {
    status: "P1",
    statusDesc: "Pending for MoveIn/Out",
    color: "geekblue"
  },
  {
    status: "P2",
    statusDesc: "Pending for Move Out Request From ESG",
    color: "geekblue"
  },
  {
    status: "R",
    statusDesc: "Ready For Approval",
    color: "geekblue"
  },
  {
    status: "S",
    statusDesc: "Suspend",
    color: "gray"
  },
  {
    status: "S1",
    statusDesc: "Suspend Temp",
    color: "gray"
  },
  {
    status: "VO",
    statusDesc: "Void",
    color: "gray"
  },
  {
    status: "X",
    statusDesc: "New - Prospect",
    color: "black-gray"
  },
  {
    status: "Total",
    statusDesc: "Total",
    color: "cyan"
  }
];

export const OPERATOR = {
  equalTo: "=",
  lessThan: "<",
  graterThan: ">",
  lessThanEqualTo: "<=",
  graterThanEqualTo: ">=",
  notBetween: "<>"
}

export const NotificationTypes = {
  success: "success",
  error: "error",
  warning: "warning"
}

export const LOCALSTORAGE_PATH_KEY =
{
  SELECTED_PAYMENT: "selectedPayment",
  SELECTED_VIEW_BILL_DETAILS: "selectedViewBillDetails",
  PAGE_BREADCRUMBS: "pageBreadcrumbs",
  SELECTED_ACCOUNT_PACKAGE: "selectedAccountPackage",
}

export const PAYMENTTABSNAME = {
  DETAILS: "details",
  LOGS: "logdetails",
  DIRECTIONS: "directiondetails",
  TRANSFERS: "transferdetails",
}

export const FORM_INPUT_TYPES = {
  TEXT: "text",
  TEXTAREA: "textarea",
  CHECKBOX: "checkbox",
  SWITCH: "switch",
  SELECT: "select"
}

export const ROW_SELECTION_TYPE = {
  RADIO: "radio",
  CHECKBOX: "checkbox"
}

export const DATE_FORMAT = {
  P2C: "YYYY-MM-DD",
  DISPLAY: "MMM DD, YYYY"
}

export const PaymentOptionCreditCard = [
  { label: 'Credit Card', value: PaymentType.CreditCard }
];

export const PaymentOptionBank = [
  { label: 'Bank Draft', value: PaymentType.BankAccount }
];

export const DEFAULT_PAGE_NUMBER = 1;

export const DEFAULT_PAGE_SIZE = 25;

export const CONTRACT_TYPES = {
  PAYMENT_CONTRACT: "Payment Contract",
  DEPOSIT_CONTRACT: "Deposit",
  LEVEL_PAY: "Level Pay",
  AVERAGE_PAY: "Average Pay"
}
