import * as types from '../actions/actionTypes';
import { replaceNull } from "../utility/generalFunctions"

const billPackageReducer = (state = {}, action) => {
    switch (action.type) {
        case types.GET_BILLING_ADDRESS_FOR_BILLING_PACKAGE:
            state = {
                ...state,
                primaryBillingAddress: replaceNull(action.payload),
                primaryBillingAddressLoaded: true
            }
            break;

        case types.GET_BILLING_ADDRESS_LIST:
            state = {
                ...state,
                billingAddressList: action.payload
            }
            break;



        case types.UPDATE_BILLING_ADDRESS_FOR_BILLING_PACKAGE:
            state = {
                ...state,
                primaryBillingAddress: replaceNull(action.payload),
                addressUpdated: true
            }
            break;
        case types.UPDATE_BILLING_ADDRESS_FOR_BILLING_PACKAGE_ERROR:
            state = {
                ...state,
                updateBillingAddressError: replaceNull(action.payload),
                addressUpdated: false
            }
            break;
        case types.UPDATE_BILLING_ADDRESS_CLEAR_ERROR:
            state = {
                ...state,
                updateBillingAddressError: undefined,
                addressUpdated: undefined
            }
            break;
        default:
            break;
    }
    return state;
}
export default billPackageReducer;
