import * as React from 'react';
import { useEffect, useContext } from 'react';
import { useLocation } from 'react-router-dom';
import { Layout, Card } from 'antd';

import { Switch, Route } from 'react-router-dom'
import * as gfunctions from '../../../utility/generalFunctions'; 
import { withRouter } from "react-router"; 
import UserContext from './../../../context/UserContext'; 
import { UserProvider } from '../../../context/UserContext';
import CommonMenu from './CommonMenu';
import MobileMenu from './MobileMenu';

import "./Home.less";
import Playlists from '../Playlist/Playlists';
import Songs from '../Song/Songs';
import Artists from '../Artists/Artists';
const Home = (props) => {

    const user = useContext(UserContext);
    const Meta = Card.Meta;
    const location = useLocation();
    

    useEffect(() => {
      alert(JSON.stringify(props.location.state));
    });

    return (
        <>
            <UserProvider value={props.location.state.userInfo}>  
                <Layout className="layout">
                    <div className="customMenuBar">
                        <div className="logo appLogo" style={{ float: "left", margin: "25px 10px 2px 25px" }}>
                          
                        </div>
                        {this.state.viewportWidth > this.state.mobileBreakPoint ?
                            <div style={{ float: "right" }}>
                                <div style={{ float: "left" }}>

                                    <CommonMenu mode="horizontal" show={true} 
                                        fullName={props.userInfo.name}
                                    />

                                </div>

                            </div>
                            :
                            <MobileMenu mode="vertical"
                                 fullName={props.userInfo.name}
                             />
                        }
                    </div>
                    <Layout>
                        <div style={{ minHeight: 280 }}>
                            <Switch>
                                <Route exact path="/" component={Playlists}></Route>
                                <Route path="/home/playlists" component={Playlists} />
                                <Route path="/home/songs" component={Songs}></Route>
                                <Route path="/home/artists" component={Artists}></Route> 
                            </Switch>
                        </div>
                    </Layout>
                </Layout>
            </UserProvider>


        </>

    );
}
export default withRouter(Home);
