import React, { useState } from 'react';
import { Link, withRouter } from 'react-router-dom';
import { connect } from 'react-redux';
import { bindActionCreators } from 'redux';
import { FormattedMessage } from 'react-intl';
import { withCookies } from 'react-cookie';

import { Menu, Dropdown, Modal, Divider, HomeOutlined, CustomerServiceOutlined, UserOutlined} from 'antd';
import { isNullOrUndefined } from 'util';


import * as logoutAction from '../../../actions/logoutAction'; 
import * as gfunctions from '../../../utility/generalFunctions'; 

import * as PortalConstants from '../../../utility/constants';
import { CaretDownOutlined } from '@ant-design/icons';

const  CommonMenu =(props) => {


          const  [mode, setMode]=useState( props.menumode);


  const  doLogout = () => {
      let cookies = props.cookies; 
      cookies.remove(PortalConstants.AUTH_TOKEN, {
        path: '/'
      });
      let loginPath = '/musicmanager/login';

        this.props.history.push(loginPath);

    }


  let menu = ()=>{ return(
    <>
    <Menu style={{padding: '8px 8px 8px 8px', borderRadius: '2px'}}>


    <Menu.Item style={{fontSize:'16px'}} onClick={() => doLogout()}> 
          Logout
      </Menu.Item>
    </Menu>
    </>);
  }
    return (

           <>
            <Menu  defaultSelectedKeys={['home']}
                    style={{ float: 'left', width: '100%' }}
                    theme='light'
                    visible={this.props.show}
                    mode={this.props.mode}      >
                    <Menu.Item key='home'>
                      <Link to='/home/playlists'>
                        <HomeOutlined />
                          Playlists Home
                      </Link></Menu.Item>

                    <Menu.Item key='songs'>
                        <Link to='/home/songs'> 
                        <CustomerServiceOutlined></CustomerServiceOutlined>
                           Songs
                        </Link>
                    </Menu.Item>
                    <Menu.Item key='artists'>
                        <Link to='/home/artists'> 
                          <UserOutlined></UserOutlined>
                            Artists
                        </Link>
                    </Menu.Item>
                    <Menu.Divider style={{height:'1.5px'}} />
                   <Menu.Item key='admin'> 
                        <Dropdown 
                          overlay={menu} 
                          trigger={['click', 'hover']}>
                        <div className='ant-dropdown-link' >
                            <table>
                            <tbody>
                              <tr>
                                <td>
                                    <table>
                                    <tbody>
                                      <tr>
                                        <td style={{ lineHeight:'normal'}}>
                                          <span style={{fontWeight:'600', fontSize:'15px'}}>{props.fullName}</span>
                                        </td>
                                      </tr> 
                                      </tbody>
                                    </table> 
                                </td>
                                <td>
                                      <div style={{float:'right'}}> <CaretDownOutlined></CaretDownOutlined></div>
                                </td>
                              </tr>
                              </tbody>
                            </table> 
                        </div>  
                      </Dropdown>
                    </Menu.Item>
                </Menu>
                </>
                );

        }

export default withCookies(withRouter(CommonMenu));
