import * as types from '../actions/actionTypes';

const customerInfoReducer = (state = {}, action) => {
    switch (action.type) {
        case types.GET_CUSTOMER_INFORMATION_BY_ID:
            state = {
                ...state,
                customerInfoDetails: action.payload
            }
            break;
        case types.POST_CUSTOMER_INFORMATION_BY_ID:
            state = {
                ...state,
                updatedCustomerInfoDetails: action.payload
            }
            break;
        case types.PATCH_CUSTOMER_INFORMATION_BY_ID:
            state = {
                ...state,
                patchedCustomerInfoDetails: action.payload
            }
            break;
        case types.GET_CUSTOMER_CONTACTS_LIST_INFORMATION_BY_ID:
            state = {
                ...state,
                customerInfoContactsList: action.payload,
            }
            break;
        case types.GET_CUSTOMER_CONTACT_DETAILS_BY_CONTACT_ID:
            state = {
                ...state,
                customerContactDetails: action.payload,
            }
            break;
        case types.UPDATE_CUSTOMER_CONTACT_DETAILS_BY_CUSTOMER_ID_CONTACT_ID:
            state = {
                ...state,
                updatedCustomerContactInformation: action.payload,
            }
            break;
        case types.CREATE_CUSTOMER_CONTACT_DETAILS_BY_CONTACT_ID:
            state = {
                ...state,
                addCustomerContactInformation: action.payload,
            }
            break;
        case types.GET_CUSTOMER_PAYMENT_METHODS:
            state = {
                ...state,
                customerInfoPaymentMethods: action.payload,
            }
            break;
        case types.GET_CUSTOMER_LOGS:
            state = {
                ...state,
                customerInfoLogs: action.payload,
            }
            break;
        case types.GET_CUSTOMER_WATCH_QUEUE:
            state = {
                ...state,
                watchQueueData: action.payload,
            }
            break;
        case types.GET_CUSTOMER_ALERTS_BY_ID:
            state = {
                ...state,
                customerAlerts: action.payload,
            }
            break;
        default:
            break;
    }
    return state;
}

export default customerInfoReducer;
