import * as React from 'react';
import { useEffect, useContext } from 'react';
import { useLocation } from 'react-router-dom';
import { Layou, Card } from 'antd';
import * as gfunctions from '../../../utility/generalFunctions';
import { KeyOutlined, UserOutlined, TeamOutlined } from '@ant-design/icons';
import { withRouter } from "react-router";
import RouteWithSubRoutes from '../../common/RouteWithSubRoutes';
import { BreadcrumbsItem } from 'react-breadcrumbs-dynamic';
import UserContext from './../../../context/UserContext';
import * as Permissions from '../../../utility/permissions';
import { UserProvider } from '../../../context/UserContext';

import "./HomePage.less";
import Playlists from '../Playlist/Playlists';
import Songs from '../Song/Songs';

const Home = (props) => {

    const user = useContext(UserContext);
    const Meta = Card.Meta;
    const location = useLocation();

    useEffect(() => {
        if (!user.isAdmin()) {
            props.history.push("/musicmanager/login");
        }
    });

    return (
        <>
            <UserProvider value={props.location.state.userInfo}>  
                <Layout className="layout">
                    <div className="customMenuBar">
                        <div className="logo appLogo" style={{ float: "left", margin: "25px 10px 2px 25px" }}>
                          
                        </div>
                        {this.state.viewportWidth > this.state.mobileBreakPoint ?
                            <div style={{ float: "right" }}>
                                <div style={{ float: "left" }}>

                                    <CommonMenu mode="horizontal" show={true} 
                                        fullName={this.props.userInfo.name}
                                    />

                                </div>

                            </div>
                            :
                            <MobileMenu mode="vertical"
                                 fullName={this.props.userInfo.fullName}
                             />
                        }
                    </div>
                    <Layout>
                        <div style={{ minHeight: 280 }}>
                            <Switch>
                                <Route exact path="/" component={Playlists}></Route>
                                <Route path="/home/playlists" component={Playlists} /> {/* Use props to pas UserInfo from store*/}
                                <Route path="/home/songs" component={Songs}></Route>
                                <Route path="/home/artists" component={Artists}></Route> 
                            </Switch>
                        </div>
                    </Layout>
                </Layout>
            </UserProvider>


        </>

    );
}
export default withRouter(Home);
