import * as React from 'react';
import { useEffect, useState } from 'react'; 
import { FormattedMessage } from "react-intl";
import { withCookies } from "react-cookie"; 
import { useIntl } from 'react-intl'; 
import {LockOutlined, UserOutlined} from 'antd/lib/icon';
import { Card, Button, Input, Row, Col, Spin, Form } from "antd";
import useApi from '../../../hooks/useApi'; 
import * as PortalConstants from "../../../utility/constants";
import * as gfunctions from "../../../utility/generalFunctions";



const LoginPage =(props)=> {

  const intl = useIntl();

   const [customMarginTop, setCustomMargin] = useState(false);
   const [hasLoginError, setHasLoginError]  = useState(false);
   const  [authenticateResponse, authenticateRequest] = useApi("users", null,PortalConstants.APIMETHODS.POST);
   const [loading, setLoading] = useState(false);
   const [form] =Form.useForm();



  const showDashBoard = (userInfo) => {
    gfunctions.setAuthenticationTokens(userInfo, props.cookies);
    let authToken =  props.cookies.get(PortalConstants.AUTH_TOKEN);
    if (authToken !== undefined && authToken.length > 1) {
      props.history.push("/home/playlists");
    }
  }

  useEffect(() => {
    gfunctions.setAuthenticationTokens(props.userInfo, props.cookies); 
  });


  useEffect(() => {
    setLoading(false);
    if(authenticateResponse.data && !authenticateResponse.error && !authenticateResponse.isLoading){
      showDashBoard(authenticateResponse.data); 
    }
    else if(authenticateResponse.error && !authenticateResponse.data && !authenticateResponse.isLoading){
      setHasLoginError(true);
    }
  }, [authenticateResponse]);

  const handleSubmit = (values) => {
    let request={};
    request.username=values.username;
    request.password = values.password;
    setHasLoginError(false);
    setLoading(true);
    authenticateRequest(request, PortalConstants.APIMETHODS.POST); 
  };

  return ( 
    <>
      <Row
        type="flex"
        style={{ minHeight: 750 }}
        justify="center"
        align="middle" >
        <Col xs={24} sm={20} md={16} lg={12} xl={8} align="middle">
          <Spin  size="large" spinning={loading}>
            <Card
              bordered={false}
              className="loginContainer"
              style={{
                top: "50%",
                minHeight: "440px",
                height: "auto",
                padding: "10px 20px 0px"
              }}
            >
              <h5 style={{ fontSize: "24px", textAlign: "center" }}>
                <FormattedMessage
                  id="login.labels.sign_in"
                  // defaultMessage="To continue, please sign in below."
                  description="Login title"
                  values={{ what: "react-intl" }}
                />
              </h5>
              <Form onFinish={handleSubmit} className="login-form"> 
                  <h1 style={{ textAlign: "center" }}>
                      Rhythm and Melody
                  </h1> 
                <Form.Item>
                  <Row>
                    <Col span={24}>
                    <Form.Item name="username" label={gfunctions.showComponentLabels("login.labels.login_id")}
                            rules={
                                [{
                                    required: true,

                                    message: intl.messages["login.labels.missing_login"]
                                }
                                ]
                            }
                        >
                            <Input size="large" prefix={<UserOutlined className="inputFieldIcon"></UserOutlined>} type="text" maxLength={50} placeholder={intl.messages['login.labels.login_id']} /> 
                        </Form.Item>

                    </Col>
                  </Row>

                  <Row style={{ marginTop: "10px" }}>
                    <Col span={24}>
                    <Form.Item style={{ marginBottom: "0px" }} name="password" label={gfunctions.showComponentLabels("login.labels.password")}
                            rules={
                                [{
                                    required: true,

                                    message: intl.messages["login.labels.missing_password"]
                                }
                                ]
                            }
                        >
                            <Input size="large" prefix={<LockOutlined className="inputFieldIcon"></LockOutlined>} type="text" maxLength={50} placeholder={intl.messages['login.labels.password']} /> 
                        </Form.Item> 
                    </Col>
                  </Row>

                </Form.Item>
                <>
                  <h6
                    style={{
                      fontSize: "14px",
                      display: "flex",
                      color: "red",
                      height: "30px",
                      marginTop: "5px"
                    }}
                  >
                    {hasLoginError &&
                      (
                        <div style={{ marginTop: "12px" }}>
                          <FormattedMessage
                            id="login.labels.login_failed"
                            defaultMessage="Your Login ID or Password was incorrect"
                            description="Please Enter proper Login Id or Password"
                            values={{ what: "react-intl" }}
                          />
                        </div>
                      )}
                  </h6>
                </>
                <Form.Item>
                  <Row>
                    <Col span={24}>
                      <Button
                        size="large"
                        style={{
                          width: "100%",
                          marginTop:
                            "0px"
                        }}
                        type="primary"
                        htmlType="submit"
                        className="login-form-button"
                      >
                        <FormattedMessage
                          id="login.labels.login"
                          defaultMessage="Log in"
                          description="Log title"
                          values={{ what: "react-intl" }}
                        />
                      </Button>
                    </Col>
                  </Row>
                </Form.Item>
              </Form>
            </Card>
          </Spin>
        </Col>
      </Row>
   </>    
  );
}



export default withCookies(LoginPage);
