import * as React from 'react';
import { useEffect, useContext, useState } from 'react';
import { useLocation } from 'react-router-dom';
import { Row, List, Card, Spin } from 'antd';
import * as gfunctions from '../../../utility/generalFunctions';
import { KeyOutlined, UserOutlined, TeamOutlined } from '@ant-design/icons';
import { withRouter } from "react-router"; 
import UserContext from './../../../context/UserContext'; 
import useApi from '../../../hooks/useApi'; 
import * as PortalConstants from "../../../utility/constants"; 

//updateStatus
const Playlists = (props) => {

    const user = useContext(UserContext);
    const Meta = Card.Meta;
    const location = useLocation();
    const [loading, setLoading] = useState(false);
    const  [playlistsFetchResponse, playlistsFetchRequest] = useApi("playlists/users/"+props.user === null || props.user=== undefined? location.state.userInfo.id : props.user.id, null,PortalConstants.APIMETHODS.GET);
    const [playlistData , setPlaylistData] =useState(null);
    const [showError, setShowError] = useState(false);
    useEffect(() => {
        if(playlistData === null && !loading && !playlistsFetchResponse.isLoading){
            setLoading(true);
            playlistsFetchRequest(null,PortalConstants.APIMETHODS.GET, "playlists/users/"+props.user === null || props.user=== undefined? location.state.userInfo.id : props.user.id);
        }
    });

    useEffect(() => {
        setLoading(false);
        if(playlistsFetchResponse.data && !playlistsFetchResponse.error && !playlistsFetchResponse.isLoading && playlistsFetchResponse){
          setPlaylistData(playlistsFetchResponse.data);
        }
        else if(playlistsFetchResponse.error && !playlistsFetchResponse.data && !playlistsFetchResponse.isLoading){
          setShowError(true);
        }
      }, [playlistsFetchResponse]);
     

    return (
        <>
        <Spin spinning={loading}>
            {playlistData && playlistData!==null && loading === false &&
            <List
            grid={{ gutter: 16, column: 4 }}
            dataSource={playlistData}
            renderItem={item => (
            <List.Item>
                <Card title={item.name}>{item.description}</Card>
            </List.Item>
            )}/>
            }
         </Spin>
        </>

    );
}
export default withRouter(Playlists);
