import * as types from '../actions/actionTypes';
import { replaceNull } from "../utility/generalFunctions"

const customerReducer = (state = {}, action) => {
    switch (action.type) {
        case types.GET_CUSTOMER_LIST:
            state = {
                ...state,
                customerList: action.payload,
                searchFilters: action.searchFilters
            }
            break;
        case types.GET_CUSTOMER_SEARCH_DROPDOWN_OPTIONS:
            state = {
                ...state,
                customerSearchConfig: action.payload
            }
            break;
        case types.GET_CUSTOMER_BY_ID:
            state = {
                ...state,
                customerPortfolioData: action.payload
            }
            break;
        case types.GET_CUSTOMER_SUMMARY_BY_ID:
            state = {
                ...state,
                customerPortfolioData: action.payload
            }
            break;
        case types.GET_PREMISE_BY_ID:
            state = {
                ...state,
                premiseMeterInfo: action.payload
            }
            break;
        case types.GET_CUSTOMER_BILLS_LIST:
            state = {
                ...state,
                customerbillingDetails: action.payload
            }
            break;
        case types.GET_CUSTOMER_PAYMENT_PROFILE:
            state = {
                ...state,
                paymentProfileList: action.payload,
                paymentProfileLoaded: true
            }
            break;
        case types.POST_CUSTOMER_GENERAL_INFO_EDIT:
            state = {
                ...state,
                customerGeneralInfo: action.payload
            }
            break;
        case types.POST_PREMISE_INFO_EDIT:
            state = {
                ...state,
                premiseMeterInfo2: action.payload
            }
            break;
        case types.GET_CUSTOMER_BILL_HISTORY:
            state = {
                ...state,
                customerBillHistory: replaceNull(action.payload),
                viewBillLoaded: true
            }
            break;
        case types.GET_BILL_DETAILS_INFO:
            state = {
                ...state,
                billPortfolioDetailsList: replaceNull(action.payload),
                billPortfolioDetailsLoaded: true
            }
            break;
        case types.GET_MISC_CHARGES:
            state = {
                ...state,
                miscChargesList: replaceNull(action.payload),
                miscChargesLoaded: true
            }
            break;
        case types.GET_BILL_TAX_DETAILS:
            state = {
                ...state,
                billTaxDetailsList: replaceNull(action.payload),
                billTaxDetailsLoaded: true
            }
            break;
        case types.GET_CUSTOMER_LETTER_NOTIFICATIONS_BY_ID:
            state = {
                ...state,
                customerLettersNotificationList: action.payload
            }
            break;
        case types.GET_CUSTOMER_LETTER_IMAGE:
            state = {
                ...state,
                customerLetterImage: action.payload
            }
            break;
        case types.UPDATE_CUSTOMER_FAQ_LDC_ACCOUNT_NUMBER:
            state = {
                ...state,
                updatedLDCAccountNumberData: action.payload
            }
            break;
        case types.GET_CUSTOMER_FAQ_LDC_ACCOUNT_NUMBER:
            state = {
                ...state,
                ldcAccountNumberFormatData: action.payload,
            }
            break;  
        case types.UPDATE_CUSTOMER_FAQ_REVENUE_CLASS:
            state = {
                ...state,
                updatedRevenueClassData: action.payload
            }
            break;          
        default:
            break;
    }
    return state;
}

export default customerReducer;
