import * as types from '../actions/actionTypes';
import { replaceNull } from "../utility/generalFunctions"

const billReducer = (state = {}, action) => {
    switch (action.type) {
        case types.GET_CUSTOMER_BILL_INFO:
            state = {
                ...state,
                customerBillInformation: action.payload,
                viewBillLoaded: true
            }
            break;
        case types.GET_CUSTOMER_BILL_DETAILS:
            state = {
                ...state,
                customerBillDetails: action.payload
            }
            break;
        case types.GET_CUSTOMER_BILL_IMAGES:
            state = {
                ...state,
                customerBillImages: action.payload,
                billImagesLoaded: true
            }
            break;
        case types.GET_CUSTOMER_BILL_IMAGES_FOR_BILLLIST:
            state = {
                ...state,
                customerBillImagesForBillList: action.payload,
                billImagesLoaded: true
            }
            break;
        case types.GET_CUSTOMER_BILL_CANCEL:
            state = {
                ...state,
                customerIsValidBillToCancel: action.payload
            }
            break;
        case types.POST_CUSTOMER_BILL_CANCEL:
            state = {
                ...state,
                canceledBill: action.payload
            }
            break;
        case types.GET_CUSTOMER_BILL_COLLECTION_STEPS:
            state = {
                ...state,
                customerCollectionSteps: action.payload
            }
            break;
        case types.GET_CUSTOMER_BILL_PACKAGE_DETAILS:
            state = {
                ...state,
                customerBillPackageDetails: action.payload,
                billPackageDetailsLoaded: true
            }
            break;
        case types.UPDATE_CUSTOMER_BILL_PACKAGE_DETAILS:
            state = {
                ...state,
                updateCustomerBillPackageDetails: action.payload,
                updateBillPackageDetailsLoaded: true
            }
            break;
        case types.GET_CUSTOMER_BILL_PACKAGE_LIST:
            state = {
                ...state,
                customerBillPackageList: replaceNull(action.payload)
            }
            break;
        case types.GET_CUSTOMER_ACCOUNT_PACKAGE_LIST:
            state = {
                ...state,
                customerAccountPackageList: action.payload
            }
            break;
        case types.ADD_CUSTOMER_BILL_BY_CUSTOMERID_BILLID:
            state = {
                ...state,
                createdBillDetails: action.payload
            }
            break;
        case types.GET_CUSTOMER_BILL_PACKAGE_ALERTS:
            state = {
                ...state,
                customerBillPackageAlerts: action.payload
            }
            break;
        default:
            break;
    }
    return state;
}
export default billReducer;
