import * as React from 'react';
import { useEffect, useContext , useState} from 'react';
import { useLocation } from 'react-router-dom';
import { Layout, Card } from 'antd';
import * as PortalConstants from '../../../utility/constants';
import { Switch, Route } from 'react-router-dom'
import * as gfunctions from '../../../utility/generalFunctions'; 
import { withRouter } from "react-router"; 
import UserContext from './../../../context/UserContext'; 
import { UserProvider } from '../../../context/UserContext';
import CommonMenu from './CommonMenu';
import MobileMenu from './MobileMenu';

import "./Home.less";
import Playlists from '../Playlist/Playlists';
import Songs from '../Song/Songs';
import Artists from '../Artists/Artists';
const Home = (props) => {

    const user = useContext(UserContext);
    const Meta = Card.Meta;
    const location = useLocation();
    const [userInfo, setUserInfo] = useState(user)

    useEffect(() => { 
     
     if(userInfo === null || userInfo === undefined && (location.state.userInfo === null || location.state.userInfo=== undefined)){
        let cookies = props.cookies; 
        cookies.remove(PortalConstants.AUTH_TOKEN, {
          path: '/'
        });
        props.history.push('/musicmanager/login');
     }
     else{
         if(userInfo === null || userInfo === undefined && (location.state.userInfo!== null && location.state.userInfo!==undefined) ){
             
         }
     }
    });

    return (
        <>
            <UserProvider value={userInfo}>  
                <Layout className="layout">
                    <div>
                            
                       
                            <div style={{ float: "right" }}>
                                <div style={{ float: "left" }}> 
                                    <CommonMenu mode="horizontal" show={true} 
                                        fullName={userInfo !== null && userInfo!== undefined? userInfo.name: ""}
                                    />

                                </div>
                            </div>
                            
                    </div>
                    <Layout>
                        <div style={{ minHeight: 280 }}>
                            <Switch>
                                <Route exact path="/" component={Playlists}></Route>
                                <Route path="/home/playlists" component={Playlists} />
                                <Route path="/home/songs" component={Songs}></Route>
                                <Route path="/home/artists" component={Artists}></Route> 
                            </Switch>
                        </div>
                    </Layout>
                </Layout>
            </UserProvider>


        </>

    );
}
export default withRouter(Home);
